import Vue from 'vue'
import App from './App.vue'
import './styles/index.css'
import VueI18n from 'vue-i18n'
Vue.use(VueI18n)
Vue.config.productionTip = false

const messages = {
  en: {
    todo: {
      add: 'Add Todo',
      delete: 'Delete Todo',
      clear: 'Clear All'
    }
  },
  zh: {
    todo: {
      add: '添加任务',
      delete: '删除任务',
      clear: '清除全部'
    }
  }
};

const i18n = new VueI18n({
  locale: 'en', // 设置语言环境
  fallbackLocale: 'en', // 设置备用语言环境
  messages // 设置语言环境信息
});


new Vue({
  i18n,
  render: h => h(App),
}).$mount('#app')
